#ifdef DEBUG
TotalDuration total("Total");
#endif

/// XXX: https://www.geeksforgeeks.org/two-pointers-technique
class Solution {
public:
    vector<vector<int>> threeSum(vector<int>& nums) {
#ifdef DEBUG
        ADD_DURATION(total);
#endif
        // Apply two pointers on sorted array
        std::sort(nums.begin(),nums.end());
        vector<vector<int>> res;
        // First, fixing [i] value
        for(int i = 0; i < nums.size();) {
            // Trying to find [p] and [k] that sum up to -[i]
            for(int p = i + 1, k = nums.size() - 1; p < k;) {
                if(nums[p] + nums[k] < -nums[i]) {
                    p++;
                } else if(nums[p] + nums[k] > -nums[i]) {
                    k--;
                } else {
                    std::vector<int> solution = {nums[i],nums[p],nums[k]};
                    // Found, skip all the same pairs of [p] and [k]
                    while(p < k && solution[1] == nums[p]) {
                        p++;
                    }
                    res.emplace_back(std::move(solution));
                }
            }
            // Skipping all the mathcing [i] since we've found all solutions for it
            int j = i;
            while(++j < nums.size()) {
                if(nums[j] != nums[i]) break;
            }
            i = j;
        }
        return res;
    }
};

